mapper.update:
UPDATE CRCLM_INFO
SET APPL_NUM = #{APPL_NUM }
WHERE RECRT_YEAR = #{RECRT_YEAR } AND RECRT_SCHDL_CD = #{RECRT_SCHDL_CD } AND DEPARTMENT = #{DEPARTMENT }

mapper.select:
SELECT COUNT(*)
FROM CRCLM_INFO
WHERE RECRT_YEAR = #{RECRT_YEAR } AND CRCLM_CD = #{CRCLM_CD } AND RECRT_SCHDL_CD = #{RECRT_SCHDL_CD }
